import React, { useState } from "react";
import "../Chatbot.css";
import { useEffect } from "react";
import {Form, Col, Button} from "react-bootstrap"
import "../App";

function Chatbot(props) {
    const [userMessage, setUserMessage] = useState("");
    const [conversationHistory, setConversationHistory] = useState([]);

    const handleChange = event => {
        setUserMessage(event.target.value);
    };

    const handleSubmit = async event => {
        // preventing a default browser reloading
        event.preventDefault();
        if (!userMessage.trim()) {
            return
        };

        if (window.refreshChatbot) {
            window.refreshChatbot = false;

            fetch("/api/saveTimePoint", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    message: "{}",
                    sessionID: "ryan"
                })
            });
        }

        var msg = {
            text: userMessage,
            user: "human"
        };

        setConversationHistory(conversationHistory => [
            ...conversationHistory,
            msg
        ]);

        const response = await fetch("/api/inputText", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
                message: userMessage,
                sessionID: "ryan"
            })
        });

        const data = await response.json();
        console.log(data);

        msg = {
            text: data.message,
            user: "ai"
        };

        setConversationHistory(conversationHistory => [
            ...conversationHistory,
            msg
        ]);

        setUserMessage("");
    };

    const ChatBubble = (text, i, className) => {
        return (
            // key for react's internal id's
            <div
                key={`${className}-${i}`}
                className={`${className} chat-bubble`}
            >
                <span className="chat-content">{text}</span>
            </div>
        );
    };

    const chat = conversationHistory.map((e, index) =>
        ChatBubble(e.text, index, e.user)
    );

    const formStyle = {
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: "1vh 10%",

    }

    return (
        <div style={{ maxHeight: "100vh" }}>
            <h1>Compliance Expert, your friendly assistant</h1>
            <div className="chat-window">
                <div className="conversationHistory-view">{chat}</div>
                <div className="message-box">
                    <Form onSubmit={handleSubmit} style={formStyle}>

                                <Form.Control
                                    type="text"
                                    placeholder="Type here..."
                                    value={userMessage}
                                    onChange={handleChange}
                                    style={{marginRight:"3%"}}
                                />

                                <Button variant="primary" type="submit">
                                    Enter
                                </Button>

                    </Form>
                    {/* <form onSubmit={handleSubmit}>
                        <input
                            value={userMessage}
                            onChange={handleChange}
                            className="text-input"
                            type="text"
                            autoFocus
                            placeholder="Type your message and hit Enter to send"
                        />
                        <button
                            type="button"
                            className="send-button"
                            onClick={handleSubmit}
                        >
                            Send
                        </button>
                    </form> */}
                </div>
            </div>
            <br></br>
        </div>
    );
}

export default Chatbot;
